import{_ as o,c as r,o as l,ag as i}from"./chunks/framework.ELrfyV9R.js";const h=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"ai/2025-03-29-柔和科技卡片风.md","filePath":"ai/2025-03-29-柔和科技卡片风.md","lastUpdated":1757580971000}'),s={name:"ai/2025-03-29-柔和科技卡片风.md"};function a(n,t,e,g,u,_){return l(),r("div",null,t[0]||(t[0]=[i('<h2 id="柔和科技卡片风" tabindex="-1">柔和科技卡片风 <a class="header-anchor" href="#柔和科技卡片风" aria-label="Permalink to &quot;柔和科技卡片风&quot;">​</a></h2><h3 id="设计风格" tabindex="-1">设计风格 <a class="header-anchor" href="#设计风格" aria-label="Permalink to &quot;设计风格&quot;">​</a></h3><ul><li><strong>圆角卡片布局</strong>：使用大圆角白色或彩色卡片作为信息容器，创造友好亲和感</li><li><strong>轻柔色彩系统</strong>：主要采用淡紫、浅黄、粉色、米色等柔和色调，避免强烈视觉刺激</li><li><strong>极简留白设计</strong>：大量留白空间增强可读性，减少视觉疲劳</li><li><strong>阴影微立体</strong>：subtle阴影效果赋予界面轻微的立体感，不过分强调</li><li><strong>功能美学主义</strong>：设计服务于功能，没有多余装饰元素</li><li><strong>网格化布局</strong>：基于明确的网格系统排列卡片，保持整体秩序感</li><li><strong>渐变色点缀</strong>：部分界面使用柔和渐变作为背景，如米色到蓝色的过渡，增加现代感</li></ul><h3 id="文字排版风格" tabindex="-1">文字排版风格 <a class="header-anchor" href="#文字排版风格" aria-label="Permalink to &quot;文字排版风格&quot;">​</a></h3><ul><li><strong>数据突显处理</strong>：关键数字信息使用超大字号和加粗处理，如&quot;12,002&quot;、&quot;20x&quot;</li><li><strong>层级分明排版</strong>：标题、说明文字、数据、注释等使用明确的字号层级区分</li><li><strong>简约无衬线字体</strong>：全部采用现代简洁的无衬线字体，提升可读性</li><li><strong>文字对齐规整</strong>：在卡片内保持统一的左对齐或居中对齐方式</li><li><strong>重点色彩标识</strong>：使用蓝色等高对比度颜色标记重要术语，如&quot;tweets&quot;和&quot;threads&quot;</li><li><strong>空间呼吸感</strong>：文字块之间保持充足间距，创造&quot;呼吸&quot;空间</li><li><strong>品牌名称特殊处理</strong>：产品名称如&quot;alohi&quot;、&quot;deel.&quot;采用特殊字体或风格，强化品牌识别</li></ul><h3 id="视觉元素风格" tabindex="-1">视觉元素风格 <a class="header-anchor" href="#视觉元素风格" aria-label="Permalink to &quot;视觉元素风格&quot;">​</a></h3><ul><li><strong>微妙图标系统</strong>：使用简约线性或填充图标，大小适中不喧宾夺主</li><li><strong>进度可视化</strong>：使用环形或条状图表直观展示进度，如年度完成百分比</li><li><strong>色彩编码信息</strong>：不同卡片使用不同色彩，便于快速区分功能模块</li><li><strong>品牌标识整合</strong>：将产品logo自然融入界面，如&quot;alohi&quot;的圆形标识</li><li><strong>人物头像元素</strong>：适当使用圆形头像增加人性化特质，如客户推荐卡片</li><li><strong>几何形状装饰</strong>：使用简单几何形状作为背景装饰，如半透明圆形</li><li><strong>组件一致性</strong>：按钮、标签、选项卡等元素保持统一风格，提升系统感</li></ul>',7)]))}const q=o(s,[["render",a]]);export{h as __pageData,q as default};
