import{_ as a,o as s,c as n,b as l}from"./app.f8774bdb.js";const h=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"安装go","slug":"安装go","link":"#安装go","children":[]},{"level":2,"title":"准备go环境","slug":"准备go环境","link":"#准备go环境","children":[]},{"level":2,"title":"查看版本","slug":"查看版本","link":"#查看版本","children":[]},{"level":2,"title":"准备开发环境","slug":"准备开发环境","link":"#准备开发环境","children":[]},{"level":2,"title":"hello world","slug":"hello-world","link":"#hello-world","children":[]},{"level":2,"title":"linux 下载","slug":"linux-下载","link":"#linux-下载","children":[]},{"level":2,"title":"总结","slug":"总结","link":"#总结","children":[]},{"level":2,"title":"gin  post提交","slug":"gin-post提交","link":"#gin-post提交","children":[]},{"level":2,"title":"gin post提交","slug":"gin-post提交-1","link":"#gin-post提交-1","children":[]}],"relativePath":"javascript/2023-01-11-go.md","lastUpdated":1712178824000}'),e={name:"javascript/2023-01-11-go.md"},o=l(`<h2 id="安装go" tabindex="-1">安装go <a class="header-anchor" href="#安装go" aria-hidden="true">#</a></h2><ul><li><a href="https://time.geekbang.org/column/article/427489" target="_blank" rel="noreferrer">https://time.geekbang.org/column/article/427489</a></li></ul><h2 id="准备go环境" tabindex="-1">准备go环境 <a class="header-anchor" href="#准备go环境" aria-hidden="true">#</a></h2><ul><li>下载链接：<a href="https://go.dev/dl/" target="_blank" rel="noreferrer">https://go.dev/dl/</a></li></ul><p>在这里可以下载到window版本、mac版本以及linux下版本。下载完毕后，分别安装即可。window和mac的安装都没什么过多的废话可以说的。这里着重简单说一下linux版本。</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// 下载到linux文件，或者拷贝到linux对应文件夹后</span></span>
<span class="line"><span style="color:#babed8;">// 比如拷贝到了当前目录</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">// 将文件解压到/usr/local</span></span>
<span class="line"><span style="color:#babed8;">tar -C /usr/local -xzf go1.19.5.linux-amd64.tar.gz</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">// 修改环境变量</span></span>
<span class="line"><span style="color:#babed8;">export PATH=$PATH:/usr/local/go/bin</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><h2 id="查看版本" tabindex="-1">查看版本 <a class="header-anchor" href="#查看版本" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// 安装完毕之后，便可以在命令行中查看版本</span></span>
<span class="line"><span style="color:#babed8;">go version   // go version go1.19.5 linux/amd64</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">// 顺便可以查看go语言中的环境变量</span></span>
<span class="line"><span style="color:#babed8;">go env</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><h2 id="准备开发环境" tabindex="-1">准备开发环境 <a class="header-anchor" href="#准备开发环境" aria-hidden="true">#</a></h2><p>我这里使用的开发工具是Visual Studio Code，下载地址为 <a href="https://code.visualstudio.com/Download" target="_blank" rel="noreferrer">https://code.visualstudio.com/Download</a></p><p>VS Code中安装插件 Go，这里就包含了很多的功能：</p><ul><li>智能感知</li><li>代码导航</li><li>代码编辑</li><li>代码诊断</li><li>测试和调试</li><li>等等其他功能</li><li>当然你也可以下载其他插件进行安装和使用</li></ul><h2 id="hello-world" tabindex="-1">hello world <a class="header-anchor" href="#hello-world" aria-hidden="true">#</a></h2><p>找到一个位置，进行查看创建文件夹，然后用VSCode打开文件夹，并在文件夹中新建main.go的文件 在main.go中的代码如下</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">package main</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">import &quot;fmt&quot;</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">func main() {</span></span>
<span class="line"><span style="color:#babed8;">	fmt.Println(&quot;hello, world&quot;)</span></span>
<span class="line"><span style="color:#babed8;">}</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>有了代码，直接命令行中，cd到main.go相同的目录</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// 以调试的模式运行</span></span>
<span class="line"><span style="color:#babed8;">go run main.go  </span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">// 或者在其他目录通过相对路径的方式进行运行</span></span>
<span class="line"><span style="color:#babed8;">go run /xx/xx/main.go</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>则可以看到控制台下输出了hello world,则表明我们的开发环境搭建成功了。</p><p>如果go build发生错误</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">go: go.mod file not found in current directory or any parent directory; see &#39;go help modules&#39;</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>则需要我们进行初始化项目</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// godemo则为module  name</span></span>
<span class="line"><span style="color:#babed8;">go mod init godemo</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>执行完毕后，则可以看到项目目录下多了一个go.mod文件</p><p>如果有其它的项目依赖，则需要执行安装依赖的命令</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// 安装项目的依赖</span></span>
<span class="line"><span style="color:#babed8;">go mod tidy </span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>OK之后，再重新运行go build，则项目下则会生成一个godemo.exe文件，直接运行后同样可以看到hello world的控制台输出。</p><h2 id="linux-下载" tabindex="-1">linux 下载 <a class="header-anchor" href="#linux-下载" aria-hidden="true">#</a></h2><ul><li><a href="https://go.dev/dl/" target="_blank" rel="noreferrer">https://go.dev/dl/</a></li><li><a href="https://juejin.cn/post/6844903756773998600" target="_blank" rel="noreferrer">https://juejin.cn/post/6844903756773998600</a></li></ul><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-hidden="true">#</a></h2><p>前端从零入坑go后端开发(一)：搭环境、打印hello world、尝试最简api服务、部署linux、最后做个小规划</p><p>年后跟公司另外一个前端，开始从零学习go，有时间就学习一点，把点滴记录总结下来，方便后续的查看和学习。如果有前端有兴趣的也可以一起加入学习。</p><p>通过简单的搭建环境，输出hello world，再到最简单的承载web服务，部署到linux服务器。初步来其实跟nodejs非常相似，只是依赖发生了变化而已，这个入门相对来说还是非常简单的，不会劝退太多的人。接下来有时间我会继续学习搭建后端服务接口的一个小框架。</p><ul><li>gin轻量级的web框架</li><li>gorm性能极高的orm操作数据库 Mysql 的框架</li><li>go-redis go中操作redis</li><li>amqp091-go go中操作rabbitmq</li><li>consul 服务发现和服务治理</li><li>go-micro 构建微服务的基础框架</li><li>golang-jwt 登录token认证</li><li>授权体系</li><li>等等，暂时想到这么多，会根据业务的调整慢慢的来优化这一条线路</li></ul><h2 id="gin-post提交" tabindex="-1">gin post提交 <a class="header-anchor" href="#gin-post提交" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">// @Param account formData string true &quot;账号&quot;</span></span>
<span class="line"><span style="color:#babed8;">// @Param password formData string true &quot;密码&quot;</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">	account := ctx.PostForm(&quot;account&quot;)</span></span>
<span class="line"><span style="color:#babed8;">  password := ctx.PostForm(&quot;account&quot;)</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><h2 id="gin-post提交-1" tabindex="-1">gin post提交 <a class="header-anchor" href="#gin-post提交-1" aria-hidden="true">#</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#babed8;">{</span></span>
<span class="line"><span style="color:#babed8;"> &quot;account&quot;: &quot;11&quot;,</span></span>
<span class="line"><span style="color:#babed8;">&quot;password&quot;: &quot;22&quot;</span></span>
<span class="line"><span style="color:#babed8;">}</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">// @Param loginModel body LoginModel true &quot;User information&quot;</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">	var loginModel LoginModel</span></span>
<span class="line"><span style="color:#babed8;">	if err := ctx.ShouldBindJSON(&amp;loginModel); err != nil {</span></span>
<span class="line"><span style="color:#babed8;">		return dto.SetResponseFailure(&quot;err--err--err--err&quot;)</span></span>
<span class="line"><span style="color:#babed8;">	}</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div>`,37),p=[o];function t(i,r,c,d,b,g){return s(),n("div",null,p)}const y=a(e,[["render",t]]);export{h as __pageData,y as default};
